package com.nybble.propify.carriershipping.service;

import static org.mockito.ArgumentMatchers.anyString;
import static org.mockito.Mockito.times;
import static org.mockito.Mockito.verify;
import static org.mockito.Mockito.when;

import java.util.Optional;

import org.junit.Before;
import org.junit.Test;
import org.junit.runner.RunWith;
import org.springframework.boot.test.mock.mockito.MockBean;
import org.springframework.test.context.junit4.SpringRunner;

import com.nybble.propify.carriershipping.exception.CarrierLabelNotFoundException;
import com.nybble.propify.carriershipping.mapper.CarrierShipmentMapper;
import com.nybble.propify.carriershipping.model.CarrierShipment;
import com.nybble.propify.carriershipping.service.impl.ShippingLabelServiceImpl;

@RunWith(SpringRunner.class)
public class ShippingLabelServiceTest {

    private ShippingLabelServiceImpl shippingLabelServiceImpl;

    @MockBean
    private CarrierShipmentMapper carrierShipmentMapper;

    @Before
    public void setUp() {
        shippingLabelServiceImpl = new ShippingLabelServiceImpl(carrierShipmentMapper);
    }

    @Test
    public void generateLabel_whenLabelIsOk_returnPdf() {
        CarrierShipment carrierShipment = new CarrierShipment();
        carrierShipment.setShippingLabelImage(getShippingLabel());
        Optional<CarrierShipment> shipment = Optional.of(carrierShipment);

        when(carrierShipmentMapper.getByLabelId(anyString())).thenReturn(shipment);

        shippingLabelServiceImpl.generateLabel("4da2681d-f5aa-419f-bc17-3cca19ad11c2");

        verify(carrierShipmentMapper, times(1)).getByLabelId(anyString());

    }

    @Test(expected = CarrierLabelNotFoundException.class)
    public void generateLabel_whenLabelNotFound_returnException() {
        Optional<CarrierShipment> shipment = Optional.empty();

        when(carrierShipmentMapper.getByLabelId(anyString())).thenReturn(shipment);

        shippingLabelServiceImpl.generateLabel("4da2681d-f5aa-419f-bc17-3cca19ad11c2");

        verify(carrierShipmentMapper, times(1)).getByLabelId(anyString());

    }

    private String getShippingLabel() {
        return "";
    }
}
